pragma solidity ^0.5.0;
library StringUtils{}


contract IoTDevices {
  // custom types
  struct Device {
    uint id;
    address seller;
    string name;
    string description;
    uint256 price;
    uint switchon;
    string deviceid;
    uint8 red; // 0-255
    uint8 green;
    uint8 blue;
    uint8 intensity; // 0-100
    uint8 class; //lightbulbs for 0, plugs for 1.
  }

  // state variables
  mapping (uint => Device) public  deviceslist;
  uint  deviceCounter;

  // events
  event LogAddDevice(
    uint indexed _id,
    address indexed _seller,
    string _name,
    string _deviceid
  );
  event LogBuyArticle(
    uint indexed _id,
    address indexed _seller,
    string _name,
    string _deviceid
  );

  // add a device
  function addDevice(string memory _name, string memory _description, string memory _deviceid) public {

    string storage priviousname =  deviceslist[ deviceCounter].name;

    bool equal = bytes(priviousname).length == bytes(_name).length;
    if(equal) {


    } else {
          // a new device
           deviceCounter++;

          // store this device
           deviceslist[deviceCounter] = Device(
            deviceCounter,
            msg.sender,
            _name,
            _description,
            0,
            0,
            _deviceid,
            0,
            0,
            0,
            0,
            0
          );

          emit LogAddDevice(deviceCounter, msg.sender, _name, _deviceid);
    }
  }

  // fetch the number of  deviceslist in the contract
  function getNumberOfdevices() public view returns (uint) {
    return  deviceCounter;
  }

  // fetch and return all device IDs for  deviceslist still for sale
  function getDevices() public view returns (uint[] memory) {
    // prepare output array
    uint[] memory deviceIds = new uint[](deviceCounter);

    uint numberOfDevices = 0;
    // iterate over  deviceslist
    for(uint i = 1; i <= deviceCounter;  i++) {
      // keep the ID if the device
        deviceIds[numberOfDevices] = deviceslist[i].id;
        numberOfDevices++;
    }


    // copy the deviceIds array into a smaller forSale array
    uint[] memory forSale = new uint[](numberOfDevices);
    for(uint j = 0; j < numberOfDevices; j++) {
      forSale[j] = deviceIds[j];
    }
    return forSale;
  }

}
